$(document).ready((function(){$("#follow-form .follow-button").click((function(o){o.preventDefault();var t=$(this).closest("form"),a=$(this).data("action"),l=t.attr("action");$.ajax({type:"POST",url:l,data:t.serialize(),success:function(o){"follow"===a?t.find(".follow-button").removeClass("btn-primary").addClass("btn-secondary").text("Unfollow").data("action","unfollow"):t.find(".follow-button").removeClass("btn-secondary").addClass("btn-primary").text("Follow").data("action","follow")},error:function(o,t,a){console.log(o.status+": "+o.responseText)}})}))}));